$(window).scroll(function(){
 
 $('.parallax').each(function(){
 var $elem = $(this);
 var parent_top = $elem.offset().top; 
 var window_bottom = $(window).scrollTop();
 var bannerWidth = $elem.width();
 oVal = ((window_bottom - parent_top) / 2);
 $elem.css('background-position','0 ' + (-(bannerWidth/6)+oVal) + 'px');
 });

 });